package javaJungsuk;

public class ShareNRemain {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int share;
		int remain;
		
		int a=100;
		int b=7;
		
		share=(a/b);
		remain=(a%b);
		
		System.out.println("share of "+a+" over " +b+"="+share);
		System.out.println("remain of "+a+ " over "+ b+"="+remain);

	}

}
